package leetcode.task30

import spock.lang.Shared;
import spock.lang.Specification;

abstract class SubstringWithConcatenationOfAllWordsSpec extends Specification {

    @Shared
    SubstringWithConcatenationOfAllWords solution

    def "test solution"() {
        expect:
        solution.findSubstring(s, words as String[]).toSet() == expected.toSet()

        where:
        s                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            | words                                                                                                                                            | expected
        "barfoothefoobarman"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                | ["foo", "bar"]                                                                                                                                   | [0, 9]
        "wordgoodgoodgoodbestword"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          | ["word", "good", "best", "word"]                                                                                                                 | []
        "barfoofoobarthefoobarman"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          | ["bar", "foo", "the"]                                                                                                                            | [6, 9, 12]
        "foobarfoobar"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      | ["foo", "bar"]                                                                                                                                   | [0, 3, 6]
        "a"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 | ["a"]                                                                                                                                            | [0]
        "wordgoodgoodgoodbestword"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          | ["word", "good", "best", "good"]                                                                                                                 | [8]
        "" | ["dhvf", "sind", "ffsl", "yekr", "zwzq", "kpeo", "cila", "tfty", "modg", "ztjg", "ybty", "heqg", "cpwo", "gdcj", "lnle", "sefg", "vimw", "bxcb"] | [935]
    }

}

class SubstringWithConcatenationOfAllWordsWithPermutationsSpec extends SubstringWithConcatenationOfAllWordsSpec {
    def setup() {
        solution = new SubstringWithConcatenationOfAllWordsWithPermutations()
    }
}

class SubstringWithConcatenationOfAllWordsWithListSearchingSpec extends SubstringWithConcatenationOfAllWordsSpec {
    def setup() {
        solution = new SubstringWithConcatenationOfAllWordsWithListSearching()
    }
}

class SubstringWithConcatenationOfAllWordsWithMapSearchingSpec extends SubstringWithConcatenationOfAllWordsSpec {
    def setup() {
        solution = new SubstringWithConcatenationOfAllWordsWithMapSearching()
    }
}